function drawSegment(graphics, offset, pStart, pEnd) {
  if (!pStart) return;
  graphics.moveTo(pStart.x + offset.x, pStart.y + offset.y);
  graphics.lineTo(pEnd.x + offset.x, pEnd.y + offset.y);
}

